// import { eventWrapper } from '@testing-library/user-event/dist/utils'
import React from 'react'
import './Testimonial.css'
import {Swiper, SwiperSlide} from 'swiper/react'
import profilePic1 from "../../img/profile1.png"
import profilePic2 from "../../img/profile2.png"
import profilePic3 from "../../img/profile3.png"
import profilePic4 from "../../img/profile4.png"

const Testimonial = () => {
  const clients =[
    {
      img: profilePic1,
      review:
      "lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex officiis molestiae quod tempora laudantium, cumque error a nisiplaceat quea exercitationem, maiores reiciendis"
    },
    {
      img: profilePic2,
      review:
      "lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex officiis molestiae quod tempora laudantium, cumque error a nisiplaceat quea exercitationem, maiores reiciendis"
    },
    {
      img: profilePic3,
      review:
      "lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex officiis molestiae quod tempora laudantium, cumque error a nisiplaceat quea exercitationem, maiores reiciendis"
    }, 
    {
      img: profilePic4,
      review:
      "lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex officiis molestiae quod tempora laudantium, cumque error a nisiplaceat quea exercitationem, maiores reiciendis"
    }
  ]
  return (
    <div className='t-wrapper'>
      <div className='t-heading'>
        <span>Client always get</span>
        <span>Exceptional work</span>
        <span>from me...</span>
        <div className="blur t-blur1" style={{background: "var(--purple)"}}></div>
        <div className="blur t-blur2" style={{background: "skyblue"}}></div>
      </div>
       {/* slider  */}
       <Swiper>
        {clients.map((client, index)=>{
           return(
            <SwiperSlide key={index}>
              <img src="client.img" alt="" />
              <span>{client.review}</span>
            </SwiperSlide>
          )
        }
        )}
       </Swiper>
    </div>
  )
}

export default Testimonial
